import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import logo from '../../src/logo.svg';
import { ButtonContainer } from './Button';
import styled from 'styled-components'

export default class Navbar extends Component {
    render() {
        return (
            <NavWrapp className="navbar navbar-expand-sm  navbar-dark px-sm-5">
                {/*https://www.iconfinder.com/icons/1243689/call_phone_icon Create Commons (Attribution 3.0 Unported);
                https://www.iconfinder.com/Makoto_msk */}
                <Link to="/">
                 <img src={logo} style={{width: 220}} alt="store" className="navbar-brand"></img>
                </Link>
                <ul className="navbar-nav align-items-center">
                    <li className="nav-item ml-5">
                    <Link to="/" className="nav-link">
                        products :)
                    </Link>
                    </li>
                </ul>
                <Link to='/cart' className="ml-auto">
                    <ButtonContainer>
                        <span className="mr-2">
                        <i className="fas fa-cart-plus">
                        </i>
                        </span>
                        my cart
                    </ButtonContainer>
                </Link>
            </NavWrapp>
        );
    }
}

const NavWrapp = styled.nav`
background: var(--mainBlue);
.nav-link{
    color:var(--mainWhite)!important;
    font-size:1.3rem;
    text-transform:capitalize;
}
`
